/* ==UserStyle==
@name         Soyjak.st - Dark Purple
@namespace    user712345ie
@author       user712345ie
@description  soyjak.st (centered threads, image adjustments, bg image)
@version      20250423.1
@license      NONE
@preprocessor uso
==/UserStyle== */

@-moz-document domain("soyjak.st") {

    /* General dark theme base */
    :root {
        --bg: rgb(29, 33, 50);
        --shadow: rgba(0, 0, 0, .28);
    }


    body {
        overflow-x: hidden !important;
        background-color: rgb(14, 16, 25) !important;
        background-image: url("https://i.postimg.cc/SxQVWK2f/image.png") !important;
        background-repeat: no-repeat !important;
        background-position: bottom right !important;
        background-attachment: fixed !important;
        background-size: 300px auto !important; /* Resize image here */
    }


    /* Thread container centering */
    div.thread {
        max-width: 1000px !important;
        margin: 0 auto !important;
    }

    .board {
        margin: 0 auto !important;
        max-width: 1200px !important;
    }

    /* Post backgrounds and box shadows */
    .opContainer,
    .op,
    .reply,
    .catalog-mode:root .board,
    .inline .post,
    .navLinks,
    #qr,
    #qr > div {
        background: var(--bg) !important;
        box-shadow: 5px 5px 5px var(--shadow) !important;
    }

    /* Adjust image presentation */
    img {
        border: 0px solid var(--alt-bg) !important;
        max-width: 100%;
        height: auto;
    }

    a.fileThumb,
    div.post div.file .fileThumb img {
        float: left !important;
        max-width: 100%;
        height: auto;
        box-shadow: 5px 5px 5px var(--shadow);
    }

    img:hover {
        opacity: 0.3 !important;
        transition: 0.4s ease-in-out !important;
    }

    .fileText {
        margin-top: 0px !important;
        margin-left: -3px !important;
        padding: 4px !important;
        max-width: 440px !important;
        opacity: 1 !important;
    }

    .file:hover .fileText,
    .file.image-expanded .fileText {
        max-height: 22px !important;
    }

    /* QR / quick reply box */
    #qr {
        width: 300px !important;
        padding: 4px !important;
        border: 1px solid var(--bg) !important;
        border-radius: 0px !important;
        transition: all 0.2s ease, max-height 0.4s ease;
        z-index: 1 !important;
    }

    #qr .textarea textarea {
        max-height: 184px;
    }

    #qr label input[type="checkbox"] {
        opacity: 0.4 !important;
    }

    #qr label input[type="checkbox"]:hover {
        opacity: 0.7 !important;
    }

    /* Typography adjustments */
    .post.reply,
    .opContainer,
    #board-list,
    .entry,
    .nameBlock,
    .field,
    .quotelink,
    .warning,
    .catalog-thread > .subject,
    .catalog-thread > .comment {
        font-family: "Open Sans", "Segoe UI" !important;
        font-weight: 600 !important;
        font-size: 13px !important;
        color: var(--main-text) !important;
    }

    /* Hide noisy interface elements */
    .sideArrows,
    .qr-link-container,
    .bottomCtrl.desktop,
    .navLinks.desktop,
    .navLinks.navLinksBot.desktop,
    .hide-post-button,
    .close.fa.fa-times,
    .bottomlink,
    .archlistlink,
    .indexlink {
        display: none !important;
    }
}
