/* ==UserStyle==
@name             Dracula for Reddit
@namespace        github.com/druxorey/stylus-themes
@version          1.2.1
@description      Are you tired of the same boring colors on Reddit? Then try this modern color style based on the Dracula theme
@author           Druxorey
@license          GNU General Public License v3.0
==/UserStyle== */

@-moz-document domain("reddit.com") {

    * {
        --bg-lighter: #585B6E;
        --bg-light: #44475A;
        --bg: #040B0F;
        --bg-dark: #040B0F;
        --bg-darker: #040B0F;

        --fg: #F8F8F2;
        --fg-dark: #70c1b3;

        --comment: #6272A4;

        --red-light: #ff6e6e;
        --red: #ff5555;
        --red-dark: #E63C3C;
        --orange-light: #FFD185;
        --orange: #FFB86C;
        --orange-dark: #E69F53;
        --yellow-light: #ffffa5;
        --yellow: #F1FA8C;
        --yellow-dark: #D8E173;
        --green-light: #69ff94;
        --green: #50FA7B;
        --green-dark: #37EB62;
        --cyan-light: #a4ffff;
        --cyan: #8BE9FD;
        --cyan-dark: #72D0E4;
        --blue-light: #6DCCFF;
        --blue: #54B3FF;
        --blue-dark: #3B9AE6;
        --purple-light: #d6acff;
        --purple: #BD93F9;
        --purple-dark: #A47AE0;
        --pink-light: #ff92df;
        --pink: #FF79C6;
        --pink-dark: #E660AD;

        --accent-1: var(--purple);
        --accent-2: var(--pink);

        --color-primary-onBackground: var(--red) !important;
        --color-primary-onBackground-selected: var(--red);
        --color-neutral-border: var(--bg-dark) !important;

        --color-neutral-content: var(--accent-1) !important;
        --color-neutral-content-disabled: var(--red) !important;
        --color-neutral-content-weak: var(--fg) !important;
        --color-neutral-content-strong: var(--accent-1) !important;
        --color-neutral-background: var(--bg) !important;
        --color-neutral-background-selected: var(--accent-1) !important;
        --color-neutral-background-weak: var(--bg-dark) !important;
        --color-neutral-background-medium: var(--red) !important;
        --color-neutral-background-strong: var(--bg) !important;
        --color-neutral-background-hover: var(--bg-darker) !important;
        --color-neutral-border-weak: transparent !important;
        --color-neutral-border-strong: var(--accent-1) !important;
        --color-tone-1: var(--accent-1) !important;
        --color-tone-2: var(--accent-2) !important;
        --color-tone-3: var(--fg) !important;
        --color-tone-4: var(--bg-light) !important;
        --color-tone-5: transparent !important;
        --color-tone-6: var(--bg-lighter) !important;
        --color-tone-7: var(--bg) !important;
        --color-primary: var(--blue) !important;
        --color-primary-hover: var(--accent-2) !important;
        --color-primary-background: var(--bg-light) !important;
        --color-primary-background-hover: var(--blue) !important;
        --color-primary-background-selected: var(--blue) !important;
        --color-secondary: var(--fg) !important;
        --color-secondary-hover: var(--fg-dark);
        --color-secondary-weak: var(--accent-1) !important;
        --color-secondary-background: var(--bg) !important;
        --color-secondary-background-hover: var(--bg-light) !important;
        --color-secondary-background-selected: var(--bg-light) !important;
        --shreddit-content-background: transparent !important;

        --button-border-color: var(--yellow);
        --button-border-color-activated: transparent !important;
        --button-color-background-activated: var(--bg-light) !important;
        --button-color-text-activated: var(--accent-1) !important;
        --button-color-overlay: var(--yellow) !important;
        --button-border-color-hover: transparent !important;
        --button-color-background-hover: var(--bg-light) !important;
        --button-color-text: transparent !important;

        --color-button-secondary-text: var(--fg) !important;
        --color-button-secondary-text-activated: var(--fg) !important;
        --color-button-secondary-text-disabled: var(--fg) !important;
        --color-button-secondary-border: transparent !important;
        --color-button-secondary-background: var(--bg) !important;
        --color-button-secondary-background-focus: var(--yellow) !important;
        --color-button-secondary-background-hover: var(--bg-lighter) !important;
        --color-button-secondary-background-activated: var(--yellow) !important;
        --color-button-secondary-background-disabled: var(--yellow) !important;

        --color-action-upvote: var(--green);
        --color-action-downvote: var(--red);
        --color-media-background: var(--bg-light);
        --color-media-onbackground: var(--accent-1);
        --color-media-onbackground-disabled: var(--bg-lighter);

        --newCommunityTheme-bodyText: var(--fg);
        --newCommunityTheme-metaText: var(--orange);
        --newCommunityTheme-button: var(--fg);
        --newCommunityTheme-body: var(--bg);
        --newRedditTheme-actionIcon: var(--fg);
        --newCommunityTheme-line: transparent;
        --newRedditTheme-line: transparent;
        --newRedditTheme-linkText: var(--accent-1);
        --newCommunityTheme-navIconFaded10: var(--orange);
        --newCommunityTheme-active: var(--orange);
        --newCommunityTheme-field: var(--bg-darker);
        --newRedditTheme-button: var(--accent-1);
        --newCommunityTheme-actionIcon: var(--accent-1);
        --newCommunityTheme-bodyTextAlpha20: transparent;
        --newRedditTheme-post: var(--bg);
        --newRedditTheme-body: var(--bg);
        --newRedditTheme-field: var(--bg-dark);
        --newCommunityTheme-navIcon: var(--fg);
        --newRedditTheme-actionIconAlpha50: var(--orange);
        --newRedditTheme-buttonAlpha05: var(--bg-light);
        --newRedditTheme-bodyText: var(--fg);
        --newCommunityTheme-buttonAlpha50: var(--bg-lighter);
        --pseudo-before-background: var(--bg-dark) !important;
        --newRedditTheme-metaText: var(--fg);
        --newRedditTheme-navIconFaded10: var(--bg);

        --newCommunityTheme-postLine: transparent;
        --newRedditTheme-widgetColors-sidebarWidgetBackgroundColor: var(--bg);
        --newCommunityTheme-widgetColors-sidebarWidgetHeaderColor: var(--orange);
        --newRedditTheme-widgetColors-sidebarWidgetBorderColor: transparent;
        --post-icon-color: var(--orange);
        --newCommunityTheme-postIcon: var(--accent-1);
        --posttitletextcolor: var(--fg);

        color: var(--fg);

    }

    header>:last-child {
        background-color: var(--bg-dark);
    }

    nav,
    #left-sidebar {
        background-color: var(--bg-dark);
    }

    .bg-neutral-background {
        background-color: var(--bg-dark);
    }

    svg {
        fill: var(--fg);
    }

    .theme-beta,
    ._24UNt1hkbrZxLzs5vkvuDh,
    ._1VP69d9lk-Wk9zokOaylL {
        background-color: var(--bg) !important;
    }

    ._2FKpII1jz0h6xCAw1kQAvS {
        background-color: var(--green) !important;
    }

    ._1poyrkZ7g36PawDueRza-J,
    ._11R7M_VOgKO1RJyRSRErT3,
    ._1Qs6zz6oqdrQbR7yE_ntfY {
        background-color: var(--bg) !important;
    }

    ._3AStxql1mQsrZuUIFP9xSg *,
    .nU4Je7n-eSXStTBAPMYt8 * {
        color: var(--accent-1) !important;
    }

}

/* ==UserStyle==
@name             Dracula for Youtube
@namespace        github.com/druxorey/dracula-for-stylus
@version          1.1.1
@description      Are you tired of the same boring colors on Youtube? Then try this modern color style based on the Dracula theme
@author           Druxorey
@license          GNU General Public License v3.0
@preprocessor     less
@var select accent-color "Accent Color" {
	"Red": "red",
	"Orange": "orange",
	"Yellow": "yellow",
	"Green": "green",
	"Blue": "blue",
	"Cyan": "cyan",
	"Purple": "purple",
	"Pink": "pink",
}

@var checkbox media-bar "Enable black bars" 0
==/UserStyle== */

@-moz-document url-prefix("https://www.youtube.com/") {

	:root {
		--color-comment:  #7397B1;

		--color-red-1:  #7397B1;
		--color-red-2:  #7397B1;
		--color-red-3: #F3F4F6;
		--color-orange-1: #FFD185;
		--color-orange-2: #FFB86C;
		--color-orange-3: #E69F53;
		--color-yellow-1: #FFFFA5;
		--color-yellow-2: #F1FA8C;
		--color-yellow-3: #D8E173;
		--color-green-1: #69FF94;
		--color-green-2: #50FA7B;
		--color-green-3: #37EB62;
		--color-cyan-1: #A4FFFF;
		--color-cyan-2: #8BE9FD;
		--color-cyan-3: #72D0E4;
		--color-blue-1: #6DCCFF;
		--color-blue-2: #54B3FF;
		--color-blue-3: #3B9AE6;
		--color-purple-1: #D6ACFF;
		--color-purple-2: #BD93F9;
		--color-purple-3: #A47AE0;
		--color-pink-1: #FF92DF;
		--color-pink-2: #FF79C6;
		--color-pink-3: #E660AD;

		--color-accent-1: e("var(--color-@{accent-color}-1)");
		--color-accent-2: e("var(--color-@{accent-color}-2)");
		--color-accent-3: e("var(--color-@{accent-color}-3)");
	}

	:root:not([dark]) {
		--color-background-5:  #7397B1;
		--color-background-4:  #7397B1;
		--color-background-3:  #7397B1;
		--color-background-2:  #7397B1;
		--color-background-1:  #7397B1;

		--color-foreground-1: #44475A;
		--color-foreground-2: #353747;
		--color-foreground-3: #282A36;

		--color-shadow-1: #F3F4F6;
		--color-shadow-2: #C4C7D4;
		--color-shadow-3: #B2B5C2;

		background: var(--color-background-4) !important;
	}

	:root[dark] {
		--color-background-1: #282a2e;
		--color-background-2: #282a2e;
		--color-background-3: #282a2e;
		--color-background-4: #282a2e;
		--color-background-5: #282a2e;

		--color-foreground-1: #B2B5C2;
		--color-foreground-2: #B2B5C2;
		--color-foreground-3: #B2B5C2;

		--color-shadow-1: #353747;
		--color-shadow-2: #21222C;
		--color-shadow-3: #1A1C24;

		background: var(--color-background-4) !important;
	}

	[fill="#B2B5C2"],
	[fill="#B2B5C2"],
	[fill="#B2B5C2"],
	[fill="#B2B5C2"],
	[fill="#B2B5C2"] {
		fill: var(--color-accent-2) !important;
	}

	&,
	[dark],
	[system-icons],
	[darker-dark-theme],
	&[dark],
	&[system-icons],
	&[darker-dark-theme] {
		--yt-spec-raised-background: var(--color-background-3);
		--yt-spec-general-background-a: var(--color-background-4);
		--yt-spec-additive-background: var(--color-background-2);
		--yt-spec-badge-chip-background: var(--color-background-3);
		--yt-spec-base-background: var(--color-background-4);
		--yt-spec-brand-background-primary: var(--color-background-4);
		--yt-spec-menu-background: var(--color-background-3);
		--yt-spec-text-secondary: var(--color-accent-2);
		--yt-spec-text-primary: var(--color-foreground-1);
		--yt-spec-icon-active-other: var(--color-foreground-1);
		--yt-spec-wordmark-text: var(--color-foreground-1);
		--yt-spec-button-chip-background-hover: var(--color-accent-2);
	}

	.ytSuggestionComponentRemoveLinkDark {
		color: var(--color-accent-2);
	}

	.ytp-progress-list {
		background: var(--color-background-2);
	}

	.ytp-load-progress {
		background: var(--color-foreground-3);
	}

	.ytSearchboxComponentInputBoxDark,
	.ytSearchboxComponentSearchButtonDark {
		background-color: var(--color-background-3);
		color: var(--color-foreground-1);
		border-color: var(--color-background-3);
	}

	.ytSearchboxComponentSuggestionsContainer,
	.ytSearchboxComponentSuggestionsContainerDark {
	    background: var(--yt-spec-raised-background) !important;
	    border-color: var(--yt-spec-raised-background) !important;
	}

	.ytSearchboxComponentInputBoxDark.ytSearchboxComponentInputBoxHasFocus {
		border-color: var(--color-accent-3);
	}

	.yt-spec-icon-badge-shape--type-notification .yt-spec-icon-badge-shape__badge {
		background-color: var(--color-accent-2);
	}

	.yt-core-attributed-string--link-inherit-color .yt-core-attributed-string__link--call-to-action-color {
		color: var(--color-accent-2);
	}

	.yt-content-metadata-view-model-wiz--medium-text .yt-content-metadata-view-model-wiz__metadata-text {
		color: var(--color-accent-2);
	}

	.yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--text {
		color: var(--color-comment);
	}

	#content.ytd-rich-item-renderer ytd-post-renderer.ytd-rich-item-renderer,
	#content.ytd-rich-item-renderer ytd-shared-post-renderer.ytd-rich-item-renderer,
	#content.ytd-rich-item-renderer ytd-mini-game-card-view-model.ytd-rich-item-renderer {
		background-color: var(--color-background-2);
		border: none;
	}

	.html5-video-player,
	.ytp-contextmenu {
		background-color: if(@media-bar =1, #000, var(--color-background-4));
	}

	.yt-spec-icon-badge-shape--style-overlay .yt-spec-icon-badge-shape__icon {
		color: var(--color-foreground-1);
	}

	.yt-page-navigation-progress {
		background: var(--color-accent-2) !important;
	}

	ytd-searchbox[has-focus] #container.ytd-searchbox {
		border-color: var(--color-background-1);
	}

	.yt-spec-button-shape-next--mono {
		&.yt-spec-button-shape-next--tonal {
			background-color: var(--color-background-2);
			color: var(--color-foreground-1);

			[fill="rgb(3,3,3)"],
			[fill="rgb(0,0,0)"],
			[fill="rgb(255,255,255)"] {
				fill: var(--color-pink-2) !important;
			}

			[stroke="rgb(0,0,0)"],
			[stroke="rgb(255,255,255)"] {
				stroke: #70c1b3 !important;
			}

			/* Accent for filled-in thumbs up (like button) */
			[animated-icon-type="LIKE"] {
				g path[fill] {
					fill: var(--color-accent-2) !important;

					+[stroke] {
						stroke: #70c1b3 !important;
					}
				}
			}

			&:hover {
				background-color: var(--color-background-1);
			}
		}

		&.yt-spec-button-shape-next--outline {
            background: var(--color-background-2);
			color: var(--color-foreground-1);
			border-color: var(--color-background-2);

			&:hover {
				background-color: var(--color-background-1);
			}
		}

		&.yt-spec-button-shape-next--text {
			color: var(--color-foreground-3);

			/* Accent for filled-in thumbs up (like button) */
			path[d="M3,11h3v10H3V11z M18.77,11h-4.23l1.52-4.94C16.38,5.03,15.54,4,14.38,4c-0.58,0-1.14,0.24-1.52,0.65L7,11v10h10.43 c1.06,0,1.98-0.67,2.19-1.61l1.34-6C21.23,12.15,20.18,11,18.77,11z"] {
				fill: #70c1b3;
			}

			&:hover {
				background-color: var(--color-background-2);
			}
		}

		&.yt-spec-button-shape-next--filled {
			background-color: var(--color-accent-2);
			color: var(--color-foreground-1);

			&:hover {
				background-color: var(--color-accent-3);
			}
		}
	}

	ytd-playlist-panel-video-renderer[can-reorder]:hover,
	ytd-playlist-panel-video-renderer[can-reorder][menu-opened],
	ytd-playlist-panel-video-renderer[can-reorder].dragging {
		box-shadow: 0 2px 8px var(--color-shadow-2);
	}

	.ytp-swatch-background-color,
	.ytp-play-progress {
		background: var(--color-accent-2) !important;
	}

	.yt_to_text_container {
		background: var(--color-background-2);
		color: var(--color-foreground-1);
	}

	.yt_to_text_default_btn {
		background: var(--color-background-1);		
		color: var(--color-foreground-1);
	}

	.yt_to_text_default_btn.selected {
		background: var(--color-accent-2);
		color: var(--color-foreground-1);
	}

	.yt_to_text_footer_contact-us {
		span {
			color: var(--color-accent-2);
		}
	}

	.yt_to_text_transcript_text_timestamp {
		color: var(--color-accent-2);
	}

	.yt-spec-button-shape-next--overlay.yt-spec-button-shape-next--tonal,
	.yt-spec-button-shape-next--overlay.yt-spec-button-shape-next--text {
		background: var(--color-background-3);
	}

	.yt-spec-button-shape-next--overlay.yt-spec-button-shape-next--tonal:hover,
	.yt-spec-button-shape-next--overlay.yt-spec-button-shape-next--text:hover {
		background: var(--color-background-1);
	}

	#yt_to_text_footer_pro_transcript,
	#yt_to_text_footer_summary{
		background: var(--color-accent-2);
		color: var(--color-foreground-1);
	}

	#yt_to_text_visibility_icon path {
		stroke: var(--color-accent-2);
	}

	#yt_to_text_profile path {
		stroke: var(--color-accent-2);
		stroke-width: 0.8;
	}
}

/* ==UserStyle==
@name             Dracula for GitHub
@namespace        github.com/druxorey/dracula-for-stylus
@version          1.3.6
@description      Are you tired of the same boring colors on GitHub? Then try this modern color style based on the Dracula theme
@author           Druxorey
@license          GNU General Public License v3.0
@preprocessor     less
@var select dracula-style "Style" {
  "Dark": "dark",
  "Light": "light",
}
@var select accent-color "Accent Color" {
	"Red": "#FF5555",
	"Orange": "#FFB86C",
	"Yellow": "#F1FA8C",
	"Green": "#50FA7B",
	"Blue": "#54B3FF",
	"Cyan": "#8BE9FD",
	"Purple": "#BD93F9",
	"Pink": "#FF79C6",
}
==/UserStyle== */

@-moz-document domain("github.com") {

    :root {
        --color-dark-background-1: #282a2e;
		--color-dark-background-2: #282a2e;
		--color-dark-background-3: #282a2e;
        --color-dark-background-4: #282a2e;
		--color-dark-background-5: #282a2e;
        --color-dark-border: #70c1b3;

		--color-dark-foreground-1: #F3F4F6;
		--color-dark-foreground-2: #70c1b3;
		--color-dark-foreground-3: #F3F4F6;

		--color-light-background-3: #F3F4F6;
		--color-light-background-4: #E8E8EE;
		--color-light-background-2: #DCDDE5;
		--color-light-background-1: #D0D2DC;
		--color-light-background-5: #C4C7D4;
        --color-light-border: #70c1b3;

		--color-light-foreground-3: #282a2e;
		--color-light-foreground-2: #282a2e;
		--color-light-foreground-1: #282a2e;

		--color-comment: #70c1b3;

		--color-red-1: #F3F4F6;
		--color-red-2: #F3F4F6;
		--color-red-3: #F3F4F6;
		--color-orange-1: #F3F4F6;
		--color-orange-2: #F3F4F6;
		--color-orange-3: #F3F4F6;
		--color-yellow-1: #F3F4F6;
		--color-yellow-2: #F3F4F6;
		--color-yellow-3: #F3F4F6;
		--color-green-1: #F3F4F6;
		--color-green-2: #70c1b3;
		--color-green-3: #70c1b3;
		--color-cyan-1: #F3F4F6;
		--color-cyan-2: #F3F4F6;
		--color-cyan-3: #F3F4F6;
		--color-blue-1: #F3F4F6;
		--color-blue-2: #F3F4F6;
		--color-blue-3: #F3F4F6;
		--color-purple-1: #F3F4F6;
		--color-purple-2: #F3F4F6;
		--color-purple-3: #F3F4F6;
		--color-pink-1: #F3F4F6;
		--color-pink-2: #F3F4F6;
		--color-pink-3: #F3F4F6;

		--color-accent: @accent-color;
	}

	html {background-color: e("var(--color-@{dracula-style}-background-3)") !important;}

	body {
		--buttonCounter-default-bgColor-rest: e("var(--color-@{dracula-style}-background-3)");

		--button-danger-bgColor-rest: e("var(--color-@{dracula-style}-background-4)");
		--button-default-bgColor-rest: e("var(--color-@{dracula-style}-background-4)");
		--button-default-borderColor-disabled: transparent;
		--button-default-borderColor-rest: transparent;
		--button-default-bgColor-disabled: e("var(--color-@{dracula-style}-background-4)");
		--button-default-bgColor-hover: e("var(--color-@{dracula-style}-background-2)");
		--button-default-borderColor-hover: transparent;
		--button-default-bgColor-active: e("var(--color-@{dracula-style}-background-2)");
		--button-default-borderColor-active: transparent;
		--button-default-fgColor-rest: e("var(--color-@{dracula-style}-foreground-1)");
		--button-primary-fgColor-disabled: e("var(--color-@{dracula-style}-foreground-3)");
		--button-star-iconColor: var(--color-yellow-2);

		--button-primary-bgColor-rest: var(--color-accent);
		--button-primary-bgColor-hover: var(--color-accent);
		--button-primary-bgColor-active: var(--color-accent);
		--button-primary-bgColor-disabled: var(--color-comment);
