        padding-bottom: 25px;
    }
    div#boardNavDesktop a,
    div#boardNavDesktopFoot a {
        margin-left: -3px;
        margin-right: -3px;
        padding: 0;
    }
    .persistentNav .pageJump a {
        margin: 0;
        padding-right: 5px;
    }
    div.pagelist {
        background: #101319;
        margin-top: 25px;
        margin-bottom: 25px;
        border: 0;
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
        margin-left: 25px;
        padding: 8px;
