@-moz-document domain("4chan.org"), domain("4channel.org") {
div#boardNavDesktop,
    div#boardNavDesktopFoot,
    div#boardNavMobile {
        background: #282a2e;
    }
    #boardSelectMobile {
        color: lightgray;
    }
    div#theme {
        background: #282a2e;
    }
    #theme kbd {
        color: #282a2e;
    }
    #theme-css {
        max-width: 320px;
    }
    .adg-rects {
        display: none;
    }
    @media only screen and (max-width: 480px) {
        div.postContainer {
            width: 100%;
        }
        div.opContainer {
            background: none !important;
            border: none !important;
        }
        div.post {
            max-width: none;
        }
        div.post div.postInfoM {
            border-bottom: none;
            background: none;
        }
        div.replyContainer {
            background: none;
        }
        div.postLink {
            background-color: #040B0F !important;
        }
        .backlink.mobile {
            background: none;
        }
        .button {
            background-image: none;
            border: none;
            color: white !important;
        }
        div.postLink span.info {
            color: white;
        }
        div.post div.postInfoM span.dateTime {
            color: lightgray !important;
        }
        div.post div.postInfoM span.dateTime a {
            color: white !important;
        }
        div.postLink {
            margin: 0 auto;
        }
        div.postLink a {
            color: white !important;
        }
        div.boardBanner > div.boardTitle {
            margin-top: 55px;
        }
    }
}
